START:
                xor     a
                out     (0xFE), a
                ld      hl, #user_input_method
                ld      bc, #0x726
                call    clr_mem

start_menu:
                ld      hl, #unk_A70E
                ld      bc, #0x721
                call    clr_mem
                call    clr_bitmap_memory
                ld      a, #0x43 ; 'C'
                call    fill_attr
                call    build_lookup_tbls
                ld      a, #5
                ld      (lives), a
                ld      a, r
                ld      b, a
                ld      a, (seed_3)
                add     a, b
                ld      (seed_3), a
                call    do_menu_selection
                ld      de, #start_game_tune
                call    play_audio
                call    init_special_objects
                call    init_start_location

player_dies:
                call    lose_life

loc_AFC8:
                call    loc_B115
                call    build_screen_objects
                call    loc_B097
                call    loc_CB89
                call    print_label_and_score
                call    loc_CC31

onscreen_loop:
                call    upd_monsters_maybe
                ld      ix, #graphic_objs_tbl

update_sprite_loop:
                ld      hl, #ret_from_tbl_jp
                push    hl
                ld      bc, #upd_sprite_jmp_tbl
                ld      a, 24(ix)                                       ; sprite data width (bytes)
                ld      28(ix), a                                       ; old sprite data width (bytes)
                ld      a, 25(ix)                                       ; sprite data height (lines)
                ld      29(ix), a                                       ; old sprite data height (lines)
                ld      a, 26(ix)                                       ; pixel X
                ld      30(ix), a                                       ; old pixel X
                ld      a, 27(ix)                                       ; pixel Y
                ld      31(ix), a                                       ; old pixel Y
                ld      l, 0(ix)

jump_to_tbl_entry:
                ld      h, #0
                add     hl, hl
                add     hl, bc
                ld      a, (hl)
                inc     hl
                ld      h, (hl)
                ld      l, a
                jp      (hl)
; ---------------------------------------------------------------------------

ret_from_tbl_jp:
                ld      a, r
                ld      b, a
                ld      a, (seed_2)
                inc     a
                ld      h, #0
                ld      l, a
                add     a, (hl)
                add     a, b
                ld      b, a
                ld      a, (seed_3)
                add     a, b
                ld      (seed_3), a
                ld      de, #0x20 ; ' '
                add     ix, de
                push    ix
                pop     hl
                ld      de, #upd_sprite_jmp_tbl
                and     a
                sbc     hl, de
                jp      C, update_sprite_loop

done_sprite_updates:
                ld      hl, (seed_2)
                inc     hl
                ld      (seed_2), hl
                call    list_objects_to_draw
                call    render_dynamic_objects
                ld      (tmp_SP_2), sp                                  ; don't think this is used
                call    handle_pause
                ld      a, (rendered_objs_cnt)
                neg
                add     a, #6
                ld      b, a
                jp      M, loc_B05C
                jr      Z, loc_B05C

game_delay:
                ld      hl, #0x500

loc_B055:
                dec     hl
                ld      a, l
                or      h
                jr      NZ, loc_B055
                djnz    game_delay

loc_B05C:
                ld      a, (render_status_info)
                and     a
                jr      Z, loc_B084
                xor     a
                ld      (render_status_info), a
                ld      a, (curr_room_attrib)
                call    fill_attr
                call    display_objects
                call    display_panel
                call    print_lives
                call    update_screen
                call    print_label_and_score
                call    reset_objs_wipe_flag
                ld      hl, #4
                ld      (word_A749), hl

loc_B084:
                call    audio_D5F2
                ld      ix, #graphic_objs_tbl
                ld      a, 0(ix)
                or      0x20(ix)
                jp      Z, player_dies
                jp      onscreen_loop
; ---------------------------------------------------------------------------

loc_B097:
                ld      iy, #byte_D422
                ld      b, #0x12

loc_B09D:
                ld      de, #16
                ld      a, (graphic_objs_tbl+8)                         ; plyr screen
                ld      c, a

loc_B0A4:
                add     iy, de
                ld      a, 0(iy)                                        ; graphic no
                and     a                                               ; null?
                jr      Z, loc_B0B2                                     ; no, skip
                ld      a, 8(iy)                                        ; curr scrn
                cp      c                                               ; same as plyr?
                jr      Z, loc_B0B5                                     ; yes, exit

loc_B0B2:
                djnz    loc_B0A4
                ret
; ---------------------------------------------------------------------------

loc_B0B5:
                ld      hl, # graphic_obj_2+0x80
                ld      de, #32
                push    bc
                ld      b, #0x30 ; '0'

loc_B0BE:
                ld      a, (hl)
                and     a
                jr      Z, loc_B0C7
                add     hl, de
                djnz    loc_B0BE
                pop     bc
                ret
; ---------------------------------------------------------------------------

loc_B0C7:
                ex      de, hl
                ld      a, 0(iy)
                and     #0xF8 ; ''
                cp      #0x80 ; ''
                jr      NZ, loc_B0D7
                ld      a, (unk_A70F)
                and     a
                jr      Z, loc_B111

loc_B0D7:
                set     4, 7(iy)
                push    iy
                pop     hl
                ld      bc, #0x10
                ldir
                push    iy
                pop     hl
                ex      de, hl
                ld      (hl), e
                inc     hl
                ld      (hl), d
                inc     hl
                ld      b, #0xE
                xor     a

loc_B0EE:
                ld      (hl), a
                inc     hl
                djnz    loc_B0EE
                ld      de, #0xFFE0
                add     hl, de
                push    hl
                pop     ix
                ld      a, 0(ix)
                and     #0xF8 ; ''
                cp      #0x80 ; ''
                jr      Z, loc_B111

loc_B102:
                call    do_any_objs_intersect
                jr      NC, loc_B111
                ld      a, 3(ix)
                add     a, #0xC
                ld      3(ix), a
                jr      loc_B102
; ---------------------------------------------------------------------------

loc_B111:
                pop     bc
                djnz    loc_B09D
                ret
; ---------------------------------------------------------------------------

loc_B115:
                ld      iy, # graphic_obj_2+0x60
                ld      c, #0x30 ; '0'

loc_B11B:
